function get_share_datas_from_html_inapp() {
	var shid = "shdefault",
		urlShid,
		urlSid,
		pk = "pkdefault",
		pp = "ppdefault",
		pkn = (typeof _PK != 'undefined' ? _PK : document.getElementById("page_key_name")),
		getQueryString = function(name) {
			var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)"),
				r = window.location.search.substr(1).match(reg);
			if (r != null) {
				return unescape(r[2])
			}
			return null;
		};
	urlShid = getQueryString("shid");
	urlSid = getQueryString("sid");
	if (urlShid) {
		shid = urlShid;
	} else if (urlSid) {
		shid = urlSid;
	}
	if (pkn) {
		var pknVal = pkn.value;
		if (pknVal) {
			var pkArray = pknVal.split("|");
			if (pkArray.length == 1) {
				pk = pkArray[0];
			} else if (pkArray.length >= 2) {
				pk = pkArray[0];
				pp = pkArray[1];
			}
		}
	}
	var ret = [];
	ret["shid"] = shid;
	ret["pk"] = pk;
	ret["pp"] = pp;
	return ret;
}
